import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ArrivalFlightList = () => {
    const [flights, setFlights] = useState([]);

    // Загрузка списка рейсов при монтировании компонента
    useEffect(() => {
        const loadFlights = async () => {
            try {
                const response = await axios.get('/api/arrivalflight/all');
                setFlights(response.data);
            } catch (error) {
                console.error('Ошибка при загрузке списка рейсов на прилет:', error);
            }
        };
        loadFlights();
    }, []);

    return (
        <div>
            <h2>Список прилетающих рейсов</h2>
            <ul>
                {flights.map((flight) => (
                    <li key={flight.flightId}>
                        Рейс {flight.flightId}: {flight.departureCity} → {flight.arrivalCity} (Прилет: {new Date(flight.arrivalTime).toLocaleTimeString()})
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ArrivalFlightList;
