import React, { useState } from 'react';
import ArrivalFlightForm from './components/ArrivalFlightForm';
import ArrivalFlightList from './components/ArrivalFlightList';
import './App.css';

const App = () => {
    const [refreshFlights, setRefreshFlights] = useState(false);

    const handleFlightCreated = () => {
        setRefreshFlights((prev) => !prev); // Обновляем состояние для перезагрузки списка рейсов
    };

    return (
        <div className="App">
            <h1>Админка модуля табло</h1>
            {/* Форма для создания рейсов на прилет */}
            <ArrivalFlightForm onSubmit={handleFlightCreated} />
            {/* Список прилетающих рейсов */}
            <ArrivalFlightList key={refreshFlights} />
        </div>
    );
};

export default App;
