// src/App.js
import React, { useState, useEffect } from 'react';
import FlightSettingsForm from './components/FlightSettingsForm';
import FlightList from './components/FlightList';
import { saveFlightSettings, fetchFlights } from './services/api';
import './App.css';

const App = () => {
    const [flights, setFlights] = useState([]);

    // Загрузка списка рейсов при монтировании компонента
    useEffect(() => {
        loadFlights();
    }, []);

    // Функция для загрузки списка рейсов
    const loadFlights = async () => {
        try {
            const data = await fetchFlights();
            setFlights(data);
        } catch (error) {
            console.error('Ошибка при загрузке списка рейсов:', error);
            alert('Ошибка при загрузке списка рейсов.');
        }
    };

    // Обработчик сохранения настроек
    const handleSaveSettings = async (settings) => {
        try {
            const result = await saveFlightSettings(settings);
            if (result.success) {
                alert('Настройки успешно сохранены!');
                // Обновляем страницу сразу после успешного сохранения
                window.location.reload();
                return; // Прерываем выполнение функции
            } else {
                alert('Ошибка при сохранении настроек.');
            }
        } catch (error) {
            console.error('Ошибка при сохранении настроек:', error);
            alert('Ошибка при сохранении настроек.');
        }
    };

    return (
        <div className="App">
            <h1>Админка модуля табло</h1>
            {/* Форма для сохранения настроек */}
            <FlightSettingsForm onSubmit={handleSaveSettings} />
            {/* Список рейсов */}
            <FlightList flights={flights} />
        </div>
    );
};

export default App;
