import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FlightList = () => {
    const [flights, setFlights] = useState([]);

    // Функция для загрузки списка рейсов
    const fetchFlights = async () => {
        try {
            const response = await axios.get('/api/flightsettings/flights');
            setFlights(response.data);
        } catch (error) {
            console.error('Ошибка при загрузке списка рейсов:', error);
        }
    };

    // Загружаем список рейсов при монтировании компонента
    useEffect(() => {
        fetchFlights();
    }, []);

    return (
        <div>
            <h2>Список рейсов</h2>
            <ul>
                {flights.map((flight) => (
                    <li key={flight.flightId}>
                        Рейс {flight.flightId}: {flight.cityFrom} → {flight.cityTo} (Вылет: {new Date(flight.departureTime).toLocaleTimeString()})
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default FlightList;
