import React, { useState } from 'react';
import FlightSettingsForm from './FlightSettingsForm';
import FlightList from './FlightList';

const App = () => {
    const [refreshFlights, setRefreshFlights] = useState(false);

    const handleSettingsSaved = () => {
        setRefreshFlights((prev) => !prev); // Обновляем состояние для перезагрузки списка рейсов
    };

    return (
        <div>
            <h1>Управление рейсами</h1>
            <FlightSettingsForm onSettingsSaved={handleSettingsSaved} />
            <FlightList key={refreshFlights} /> {/* key обновляется, чтобы перезагрузить компонент */}
        </div>
    );
};

export default App;
