import React, { useState } from 'react';
import axios from 'axios';

const ArrivalFlightForm = ({ onSubmit }) => {
    const [settings, setSettings] = useState({
        departureCity: '', // Город отправления
        arrivalTimeOffset: 5, // Временной интервал до прилета (в минутах)
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSettings((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/arrivalflight/create', settings);
            // Проверяем, что ответ содержит данные о рейсе
            if (response.data) {
                alert('Рейс на прилет успешно создан!');
                onSubmit(); // Обновляем список рейсов
            } else {
                alert('Ошибка при создании рейса на прилет: ответ сервера пуст.');
            }
        } catch (error) {
            console.error('Ошибка при создании рейса на прилет:', error);
            // Отображаем сообщение об ошибке из бэкенда, если оно есть
            const errorMessage = error.response?.data?.message || 'Ошибка при создании рейса на прилет.';
            alert(errorMessage);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>
                    Город отправления:
                    <input
                        type="text"
                        name="departureCity"
                        value={settings.departureCity}
                        onChange={handleChange}
                        required
                    />
                </label>
            </div>
            <div>
                <label>
                    Время до прилета (минуты):
                    <input
                        type="number"
                        name="arrivalTimeOffset"
                        value={settings.arrivalTimeOffset}
                        onChange={handleChange}
                        required
                    />
                </label>
            </div>
            <button type="submit">Создать рейс на прилет</button>
        </form>
    );
};

export default ArrivalFlightForm;
